class ClsMsgBox:
	mMessage={}
	def __init__(A):
		if len(A.mMessage)==0:A.createMessageDict()
	def get(A,pIdx,pOpt=''):
		if pOpt=='':B=A.mMessage[pIdx]
		else:B=A.mMessage[pIdx]%pOpt
		return B.replace("'%s'",'')
