urlpatterns=[path('',IndexView.as_view(),name='frontend_index'),path('<str:kind>/detail/<slug:slug>/',DetailView.as_view(),name='frontend_detail'),path('<str:kind>/list/<slug:slug>/',ListView.as_view(),name='frontend_list'),path('description/detail/',DescriptionView.as_view(),name='pages_description'),path('greeting/detail/',GreetingView.as_view(),name='pages_greeting'),path('booking/detail/',BookingView.as_view(),name='pages_booking'),path('get-calendar-ajax/<int:year>/<int:month>/',get_calendar_ajax,name='get_calendar_ajax'),path('<str:kind>/checkout/<slug:slug>/',CheckOutView.as_view(),name='frontend_checkout')]